// Ejercicio 2
// =====================

const contenedor = document.getElementById("contenedor");
const btnAgregar = document.getElementById("agregar");
const btnEliminar = document.getElementById("eliminar");

btnAgregar.addEventListener("click", () => {
  const p = document.createElement("p");
  p.textContent = "Nuevo párrafo agregado dinámicamente";
  contenedor.appendChild(p);
});

btnEliminar.addEventListener("click", () => {
  const ultimo = contenedor.lastChild;
  if (ultimo) {
    contenedor.removeChild(ultimo);
  }
});

// =====================
// Ejercicio 3
// =====================

const btnColor = document.getElementById("cambiarColor");

function colorAleatorio() {
  return "#" + Math.floor(Math.random() * 16777215).toString(16);
}

btnColor.addEventListener("click", () => {
  document.body.style.backgroundColor = colorAleatorio();
});

// =====================
// Ejercicio 4
// =====================

const btnResaltar = document.getElementById("resaltar");

btnResaltar.addEventListener("click", () => {
  const destacados = document.querySelectorAll(".destacado");
  destacados.forEach(elemento => {
    elemento.style.color = "white";
    elemento.style.backgroundColor = "orange";
    elemento.style.fontWeight = "bold";
  });
});

// =====================
// Ejercicio 5
// =====================

const btnCambiarImg = document.getElementById("cambiarImagen");
const imagen = document.getElementById("imagen");

let imgOriginal = "data:image/jpeg;base64,";
let imgAlterna = "data:image/jpeg;base64,";

btnCambiarImg.addEventListener("click", () => {
  if (imagen.getAttribute("src") === imgOriginal) {
    imagen.setAttribute("src", imgAlterna);
    imagen.setAttribute("alt", "Imagen 2");
  } else {
    imagen.setAttribute("src", imgOriginal);
    imagen.setAttribute("alt", "Imagen 1");
  }
});
